class Post {
  final String posterName;
  final String posterImage;
  final String postTime;
  final String postMessage;
  final String postImage;
  final String postLikeMessage;
  final List<Comment> comment;

  Post({
    this.comment,
    this.posterName,
    this.posterImage,
    this.postTime,
    this.postMessage,
    this.postImage,
    this.postLikeMessage,
  });
}

class Comment {
  final String commentatorName;
  final String commentatorImage;
  final String commentMessage;
  final bool loved;
  final int likeCount;
  final bool isMe;
  final bool isReply;
  final String commentTime;

  Comment(
      {this.commentMessage,
      this.commentatorName,
      this.commentatorImage,
      this.loved: false,
      this.likeCount,
      this.isMe: false,
      this.isReply: false,  this.commentTime});
}

